<script>
    import bus from '../common/bus';
    export default {
        data() {
            return {
                collapse:false
            }
        },
        computed:{
            onRoutes(){
                return this.$route.path.replace('/','');
            }
        },
        created(){
            // 通过 Event Bus 进行组件间通信，来折叠侧边栏,监听
            // bus.$on('collapse', msg => {
            //     this.collapse = msg;
            // })
        },
        methods:{
            //退出登录
            logout(){
                this.$router.push('/login');
            },
            // 侧边栏折叠
            collapseChage(){
                this.collapse = !this.collapse;
                bus.$emit('collapse', this.collapse);
            },
            //明细
            detail(){
                this.$router.push('rentHome');
            },
            //监控
            monitor(){
                this.$router.push('monitor');
            },
            //记账
            addRent(){
                // this.$router.push('chooseCatalog');
                this.$router.push('rentHouseChoose');
            },
            //功能
            func(){
                this.$router.push('function');
            },
            //我的
            me(){
                this.$router.push('me');
            }
        }
    }
</script>

